mapper.select:
        select
id, type, host_ip, host_password, ssh_port, agent_name, user_name, user_id, create_time, updated_at
        from agent_manage_config


                and id = #{id}


                and type = #{type}


                and host_ip = #{hostIp}


                and host_password = #{hostPassword}


                and ssh_port = #{sshPort}


                and agent_name = #{agentName}


                and user_name = #{userName}


                and user_id = #{userId}


                and create_time = #{createTime}


                and updated_at = #{updatedAt}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from agent_manage_config


        and id = #{id}


        and type = #{type}


        and host_ip = #{hostIp}


        and host_password = #{hostPassword}


        and ssh_port = #{sshPort}


        and agent_name = #{agentName}


        and user_name = #{userName}


        and user_id = #{userId}


        and create_time = #{createTime}


        and updated_at = #{updatedAt}

mapper.delete:
delete from agent_manage_config


        and id = #{id}


        and type = #{type}


        and host_ip = #{hostIp}


        and user_id = #{userId}
