mapper.select:
select
     id, host_ip, agent_name, user_id, config, create_time, updated_at
from agent_user_config


        and id = #{agentUserConfig.id}


        and host_ip = #{agentUserConfig.hostIp}


        and agent_name = #{agentUserConfig.agentName}


        and user_id = #{agentUserConfig.userId}


        and config = #{agentUserConfig.config}


        and create_time = #{agentUserConfig.createTime}


        and updated_at = #{agentUserConfig.updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from agent_user_config


        and id = #{id}


        and host_ip = #{hostIp}


        and agent_name = #{agentName}


        and user_id = #{userId}


        and config = #{config}


        and create_time = #{createTime}


        and updated_at = #{updatedAt}
