mapper.select:
select
    id, config_id, group_id, group_name, team_name, max_cpus, max_memory, created_at, updated_at,collection_port
from collector_user_config


        and id = #{config.id}


        and config_id = #{config.configId}


        and group_id = #{config.groupId}


        and group_name = #{config.groupName}


        and team_name = #{config.teamName}


        and max_cpus = #{config.maxCpus}


        and max_memory = #{config.maxMemory}


        and created_at = #{config.createdAt}


        and updated_at = #{config.updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from collector_user_config


        and id = #{id}


        and config_id = #{configId}


        and group_id = #{groupId}


        and group_name = #{groupName}


        and team_name = #{teamName}


        and max_cpus = #{maxCpus}


        and max_memory = #{maxMemory}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
