mapper.select:
        select
user_id, username, password, phone, email, status, role, note, create_time, update_time, login_time
        from user


                and user_id = #{userId}


                and username = #{username}


                and password = #{password}


                and phone = #{phone}


                and email = #{email}


                and status = #{status}


                and role = #{role}


                and note = #{note}


                and create_time = #{createTime}


                and update_time = #{updateTime}


                and login_time = #{loginTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and status = #{status}


        and role = #{role}


        and note = #{note}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and login_time = #{loginTime}
